// 堆排序
//   -- 稳定性：不稳定
//   -- 时间复杂度：o(nlogn)
//   -- 空间复杂度：o(1)
void CreatHeap(int a[], int i, int  n) {
// 数组是从0开始计数，所以左节点为2*i+1，右节点为2*i+2
// [0~i]是父节点的下标，n是节点总数
for (; i >= 0; --i)
{
    int left = i*2 + 1;    //左子树节点
    int right = i*2 + 2;    //右子树节点
    int j = 0;
    //选出左右子节点中最大的
    if (right  a[right] ? left : right;
    } else{
        j = left;
    }
    //交换子节点与父节点
    if (a[j] > a[i]) {
        int tmp = a[i];
        a[i] = a[j];
        a[j] = tmp;
    }
}
}
// 进行堆排序，依次选出最大值放到最后面
void HeapSort(int a[], int n) {
//初始化构造堆
CreatHeap(a, n/2-1, n);
for (int j = n-1; j >= 0; j--) {

    //最后一个元素和第一个元素进行交换
    int tmp = a[0];
    a[0] = a[j];
    a[j] = tmp;

    int i = j / 2 - 1;
    CreatHeap(a, i, j);
}
}
